    // assumes 1 frame prep stage
    void animation_phase(void) {

        int get_inactivity_period_in_seconds = (int) (last_input_activity_elapsed() / 1000);
        anim_frame_duration_reducer = 0;
        if (get_inactivity_period_in_seconds <= IDLE_ANIMATION_PERIOD_THRESHOLD) {
            current_idle_frame = (current_idle_frame + 1) % IDLE_FRAMES;
            oled_write_raw_P(idle[abs((IDLE_FRAMES - 1) - current_idle_frame)], ANIM_SIZE);
        }
        if (get_inactivity_period_in_seconds > IDLE_ANIMATION_PERIOD_THRESHOLD && get_inactivity_period_in_seconds < TAP_ANIMATION_PERIOD_THRESHOLD) {
            oled_write_raw_P(prep[0], ANIM_SIZE);  // remove if IDLE_FRAMES >1
        }
        if (get_inactivity_period_in_seconds >= TAP_ANIMATION_PERIOD_THRESHOLD) {
            anim_frame_duration_reducer = get_inactivity_period_in_seconds;
            current_tap_frame = (current_tap_frame + 1) % TAP_FRAMES;
            oled_write_raw_P(tap[abs((TAP_FRAMES - 1) - current_tap_frame)], ANIM_SIZE);
        }
    }

    if (last_input_activity_elapsed() > OLED_TIMEOUT) {
        oled_off(); // Force OLED off, otherwise it will continue being active due to the animation running
    } else {
        if (timer_elapsed32(anim_timer) > min((ANIM_FRAME_DURATION - anim_frame_duration_reducer*2), ANIM_FRAME_DURATION_FLOOR)) {
            anim_timer = timer_read32();
            animation_phase();
        }
    }
    
